definition.script:
#!/usr/bin/env groovy

def osa, common
String workspace_dir 

// Jenkins must provide these two variables as parameters or the build 
// will fail:
String from_release='stable/mitaka'
String to_release='stable/newton'

// *******************************
stage 'Pre-Deployment'
// *******************************

echo 'Load qa-jenkins-onmetal project'
node('agent-xl') {
    // Load the external functions using master since the git command
    // might not be available at the agent yet
    fileLoader.withGit('https://github.com/osic/qa-jenkins-onmetal.git', 'master', null, '') {
        common = fileLoader.load('jenkins/common.groovy');
        osa = fileLoader.load('jenkins/osa_onmetal.functions.groovy');
    }

}
// *******************************
stage 'Rolling Upgrade'
// *******************************

echo "Run pre-upgrade tasks"
node('agent-xl') {
    String main_path = '/home/ubuntu/openstack-ansible'
    osa.pre_project_upgrade(to_release, main_path)
}

echo "Run nova rolling upgrade"
node('agent-xl') {
    String main_path = '/home/ubuntu/openstack-ansible'
    String project = 'keystone'
    osa.upgrade_project(project, main_path)
}
