definition.script:
String elasticsearch_ip

stage 'Sandbox'

node('elasticsearch') {

    host_ip = '172.99.84.142'
    // Get the IP of the Elasticsearch/Kibana server
    elasticsearch_ip = sh returnStdout: true, script: 'ip addr show eth0 | grep "inet\\b" | awk \'{print $2}\' | cut -d/ -f1'
    elasticsearch_public_key = sh returnStdout: true, script: 'cat /home/ubuntu/.ssh/id_rsa.pub'
    echo elasticsearch_public_key
    sh """echo '${elasticsearch_public_key}'"""
    ssh -o StrictHostKeyChecking=no root@${host_ip} """
        echo '${elasticsearch_public_key}' >> /home/ubuntu/.ssh/authorized_keys
    """
    //sh """echo '${elasticsearch_public_key}' >> /home/ubuntu/.ssh/authorized_keys"""

}

node('onmetal-provisioner') {
    echo elasticsearch_ip
}
